# We implement a straightforward algorithm that examines every substring of length 13.
def compute():
	ans = max(digit_product(NUMBER[i : i + ADJACENT]) for i in range(len(NUMBER) - ADJACENT + 1))
	return str(ans)


def digit_product(s):
	result = 1
	for c in s:
		result *= int(c)
	return result


NUMBER = ""
ADJACENT = 13


if __name__ == "__main__":
	print(compute())
